namespace yiiswoole\web;

class Controller extends \yii\web\Controller
{
    protected static $actionInstances = [];
    public function createAction($id)
    {
        if ($id === '') {
            $id = $this->defaultAction;
        }
        $key = $this->id . $id;
        if (isset(static::$actionInstances[$key])) {
            return clone static::$actionInstances[$key];
        }

        $action = parent::createAction($id); // TODO: Change the autogenerated stub
        if ($action) {
            static::$actionInstances[$key] = $action;
        }
        return $action;
    }
}
